const remaining = graph.nodes
.filter(n => n.id !== chosen_edge.to && n.id !== chosen_edge.from);

const from = [...(graph.nodes
.find(n => n.id === chosen_edge.to).nodes ?? [chosen_edge.to])]

const to = [...(graph.nodes.find(n => n.id === chosen_edge.from).nodes ?? [chosen_edge.from])];

return [remaining
.concat([{
id: new_id, 
nodes: [...(new Set(from.concat(to))).keys()],
}])

];

let accfrom = edges.flatMap(e => [e.from]).reduce((acc, v) => { acc[v] = (acc[v] ?? 0) + 1; return acc}, {});
let accto = edges.flatMap(e => [e.to]).reduce((acc, v) => { acc[v] = (acc[v] ?? 0) + 1; return acc}, {});

return nodes.map(n => n.id).filter(n => (!isNaN(accfrom[n]) && accfrom[n] <= 1) && !accto[n] );
